import Vue from "vue";
import Vuex from "vuex";
 
Vue.use(Vuex);
 
export default new Vuex.Store({
 state: {
    userData: {}
 },
 mutations: {
    setUser (state, payload) {
      state.userData = payload
    }
 }
});
